const AuthReducer = (state , action )=>{
    switch(action.type){
        case "LOGIN_START":
            return{
                user:{},
                isFetching: true,
                error:false,
            };
        case "LOGIN_SUCCESS":
            return{
                user: action.payload,
                isFetching:false,
                error:false,
            };
        case "LOGIN_FAILURE":
            return{
                user:{},
                isFetching:false,
                error: action.payload,
            };
        case "FOLLOW":
            return{
                ...state,
                user: {
                    ...state.user,
                    follwers:[...state.user.follwers],
                    followings:[...state.user.followings, action.payload],
                },
            };
        case "UNFOLLOW":
            return{
                ...state,
                user: {
                    ...state.user,
                    follwers:[...state.user.follwers],
                    followings: [state.user.followings.filter((following)=>following !== action.payload)]
                }
            };
        default:
            return state
    }
};

export default AuthReducer;
