            if(followed){
                await axios.get("/product/"+product._id+"/unfollow", {userId:user._id});
                dispatch({type:"UNFOLLOW", payload:product._id})
            }else{
                await axios.get("/product/"+product._id+"/follow", {userId:user._id});
                dispatch({type:"FOLLOW", payload:product._id})
            }
        }catch(err){
            console.log(err)
        }
        setFollowed(!followed)
    }

    useEffect(()=>{
        const getFriends = async()=>{
            try{
                const friendList = await axios.get("/product/all");
                setFriends(friendList.data);
            }catch(err){
                console.log(err)
            }
        }
        getFriends();
    },[])  ;

    const [friends,setFriends] = useState([]);
    const [followed,setFollowed] = useState();

    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    return (
        <>
            <div className="suggestion">
                <div className="follow">
                            {friends.map((friend)=>(
                            <div className="rightbarTop">
                                <div className="rightbarTopLeft">
                                <Link to={`profile/${friend.username}`}>
                                    <img src={friend.profilePicture? PF+friend.profilePicture : PF+"pic/noAvatar.jpeg"} className="rightImg" alt="" />
                                </Link>
                                    <div className="username">
                                        <a href="#">{friend.username!==user.username? friend.username : "My Profile"}</a>
                                        <span className="rightText">{friend.username!==user.username? friend.username : ""}</span>
                                    </div>
                                </div>
                                <div>
                                    <span className="switch">{friend.username!==user.username? "Follow" : ""}</span>
                                </div>
                             </div>
                            ))}
                        </div>
                </div>
        </>
    )
}
