import "./suggestion.css"

export default function Suggestion({user}) {
    // useEffect(()=>{
    //     const getFriends = async()=>{
    //         try{
    //             const friendList = await axios.get("/product/friends/"+ user._id);
    //             setFriends(friendList.data);
    //         }catch(err){
    //             console.log(err)
    //         }
    //     }
    //     getFriends();
    // },[user])  ;
    // const [friends,setFriends] = useState([]);
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    return (
        <>
            <div className="suggestion">
                <div className="follow">
                        <div className="rightbarTop">
                            <div className="rightbarTopLeft">
                                <img src={user.profilePicture? PF+user.profilePicture : PF+"pic/noAvatar.jpeg"} className="rightImg" alt="" />
                                <div className="username">
                                    <a href="#">{user.username}</a>
                                    <span className="rightText">{user.username}</span>
                                </div>
                            </div>
                            <div>
                                <span className="switch">Follow</span>
                            </div>
                        </div>
                </div>
            </div>
        </>
    )
}
