import { useEffect, useState } from "react";
import "./suggestion.css"
import axios from "axios";

export default function Suggestion({user}) {

    useEffect(()=>{
        const getFriends = async()=>{
            try{
                const friendList = await axios.get("/product/all");
                setFriends(friendList.data);
            }catch(err){
                console.log(err)
            }
        }
        getFriends();
    },[])  ;

    const [friends,setFriends] = useState([]);

    const displayUser = () =>{
        friends.map((friend)=>{
            if (user.username !== friend.username) {
            return(
                <div className="rightbarTop">
                    <div className="rightbarTopLeft">
                        <img src={friend.profilePicture? PF+friend.profilePicture : PF+"pic/noAvatar.jpeg"} className="rightImg" alt="" />
                        <div className="username">
                            <a href="#">{friend.username}</a>
                            <span className="rightText">{friend.username}</span>
                        </div>
                    </div>
                    <div>
                       <span className="switch">Follow</span>
                    </div>
                </div>
            );
        } else{
            return(
                <div className="rightbarTop">
                    <div className="rightbarTopLeft">
                        <img src={friend.profilePicture? PF+friend.profilePicture : PF+"pic/noAvatar.jpeg"} className="rightImg" alt="" />
                        <div className="username">
                            <a href="#">{friend.username}</a>
                            <span className="rightText">{friend.username}</span>
                        </div>
                    </div>
                    <div>
                       <span className="switch">Me</span>
                    </div>
                </div>
            )
        }
    }
    )};

    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    return (
        <>
            <div className="suggestion">
                <div className="follow" onClick={{displayUser}}>

                </div>
            </div>
        </>
    )
}


