import Online from "../online/online"
import "./story.css"
// import { Users } from "../../dummyData"
import { useContext, useEffect, useState } from "react"
import { AuthContext } from "../../context/AuthContext"
import axios from "axios"

export default function Story({user}) {
    useEffect(()=>{
        const getFriends = async()=>{
            try{
                const friendList = await axios.get("/product/friends/"+ user._id);
                setFriends(friendList.data);
            }catch(err){
                console.log(err)
            }
        }
        getFriends();
    },[user])  

    const {user:currentUser} = useContext(AuthContext);
    const [friends,setFriends] = useState([]);
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;

    return (
        <div className="story">
            <div className="storyWrapper">
               {friends.map((friend)=>(
                <div className="online">
                <div className="storyItem">
                    <div className="allFriends">
                        <img src={friends.profilePicture? PF+friends.profilePicture : PF+"pic/noAvatar.jpeg"} alt="" className="storyPic"/>
                        <span className="storyText">{friends.username}</span>
                    </div>
                </div>
            </div>
               ))}
            </div>
        </div>
    )
}
